changeSet.createView:
SELECT RowNum as id , doreha.*, concat(concat(tbl_student.first_name, ' '), tbl_student.last_name) AS student, tbl_student.national_code AS student_national_code, tbl_student.emp_no , tbl_student.personnel_no, tbl_post.c_code as student_post_code, tbl_post.c_title_fa as student_post_title, tbl_department.c_hoze_title as student_hoze, tbl_department.c_omor_title as student_omor, tbl_class_student.score * 5 as nore_nahaii , tbl_class_student.pre_test_score * 5 as nore_pish FROM tbl_class_student INNER JOIN ( SELECT tbl_class.c_code AS class_code, view_complex.c_title AS complex, tbl_class.c_start_date as class_start_date, tbl_class.c_end_date as class_end_date, CASE WHEN tbl_class.c_status = 1 THEN ' ' WHEN tbl_class.c_status = 2 THEN ' ' WHEN tbl_class.c_status = 3 THEN ' ' WHEN tbl_class.c_status = 4 THEN ' ' WHEN tbl_class.c_status = 5 THEN '' END AS class_status,
CASE WHEN tbl_teacher.b_personnel = 1 THEN '' ELSE '' END AS is_personnel, concat(concat(tbl_personal_info.c_first_name_fa, ' '), tbl_personal_info.c_last_name_fa) AS teacher, tbl_teacher.c_teacher_code AS teacher_national_code, tbl_course.c_code AS course_code, tbl_course.c_title_fa AS course_titlefa, tbl_category.c_title_fa AS category_titlefa, tbl_sub_category.c_title_fa AS sub_category_titlefa, clsstu.total_std as total_std, tbl_evaluation_analysis.c_learning_grade as learning, tbl_class.id AS idd FROM tbl_evaluation_analysis INNER JOIN tbl_class ON tbl_evaluation_analysis.f_tclass = tbl_class.id LEFT JOIN view_complex ON tbl_class.complex_id = view_complex.id LEFT JOIN tbl_teacher ON tbl_class.f_teacher = tbl_teacher.id LEFT JOIN tbl_personal_info ON tbl_teacher.f_personality = tbl_personal_info.id LEFT JOIN tbl_course ON tbl_class.f_course = tbl_course.id LEFT JOIN tbl_sub_category ON tbl_course.subcategory_id = tbl_sub_category.id LEFT JOIN tbl_category ON tbl_course.category_id = tbl_category.id LEFT JOIN ( SELECT DISTINCT class_id AS classid, total_std FROM ( SELECT tbl_class_student.class_id, tbl_class_student.student_id, COUNT(*) OVER(PARTITION BY tbl_class_student.class_id) total_std FROM tbl_class_student ) WHERE total_std > 0 ) clsstu ON tbl_class.id = clsstu.classid LEFT JOIN ( SELECT SUM(pre) AS mianginpre, classid FROM ( SELECT tbl_class_student.pre_test_score AS pre, tbl_class_student.class_id AS classid FROM tbl_class_student ) GROUP BY classid ) pre ON tbl_class.id = pre.classid LEFT JOIN ( SELECT SUM(asli) AS mianginasli, classid FROM ( SELECT tbl_class_student.score AS asli, tbl_class_student.class_id AS classid FROM tbl_class_student ) GROUP BY classid ) nahaii ON tbl_class.id = nahaii.classid LEFT JOIN ( SELECT MAX(asli) AS maxasli, classid FROM ( SELECT tbl_class_student.score AS asli, tbl_class_student.class_id AS classid FROM tbl_class_student ) GROUP BY classid ) maxnahaii ON tbl_class.id = maxnahaii.classid LEFT JOIN ( SELECT MIN(asli) AS minpre, classid FROM ( SELECT tbl_class_student.pre_test_score AS asli, tbl_class_student.class_id AS classid FROM tbl_class_student ) GROUP BY classid ) minpre ON tbl_class.id = minpre.classid LEFT JOIN ( SELECT COUNT(*) AS javabdade, tbl_class_student.class_id AS classid FROM tbl_class_student WHERE tbl_class_student.score IS NOT NULL GROUP BY tbl_class_student.class_id ) javabdadeasli ON tbl_class.id = javabdadeasli.classid LEFT JOIN ( SELECT COUNT(*) AS javabdade, tbl_class_student.class_id AS classid FROM tbl_class_student WHERE tbl_class_student.pre_test_score IS NOT NULL GROUP BY tbl_class_student.class_id ) javabdadepre ON tbl_class.id = javabdadepre.classid LEFT JOIN ( SELECT COUNT(*) AS javabdade, tbl_class_student.class_id AS classid FROM tbl_class_student WHERE ( tbl_class_student.scores_state_id = 400 ) OR ( tbl_class_student.scores_state_id = 401 ) GROUP BY tbl_class_student.class_id ) ghaboolasli ON tbl_class.id = ghaboolasli.classid LEFT JOIN ( SELECT COUNT(*) AS javabdade, tbl_class_student.class_id AS classid FROM tbl_class_student WHERE tbl_class_student.scores_state_id = 403 GROUP BY tbl_class_student.class_id ) noghaboolasli ON tbl_class.id = noghaboolasli.classid
) doreha ON tbl_class_student.class_id = doreha.idd LEFT JOIN tbl_student ON tbl_class_student.student_id = tbl_student.id LEFT JOIN tbl_post ON tbl_student.f_post_id = tbl_post.id LEFT JOIN tbl_department ON tbl_student.f_department_id = tbl_department.id ORDER BY doreha.class_code

SELECT rowNum as id, term.C_TITLE_FA as term_TitleFa, personal.C_FIRST_NAME_FA as teacher_FirstName, personal.C_LAST_NAME_FA as teacher_LastName, CONCAT(CONCAT(personal.C_FIRST_NAME_FA, ' '), personal.C_LAST_NAME_FA) as teacher_FullName, tclass.N_H_DURATION as tclass_n_duration, tclass.F_INSTITUTE_ORGANIZER as tclass_organizer, tclass.C_STATUS as tclass_status, tclass.f_cancel_class_reason as class_cancel_reason_id, parameterValue.C_TITLE as class_cancel_reason_title, tclass.F_PLANNER as tclass_planner, tclass.F_SUPERVISOR as tclass_supervisor, tclass.c_created_by as c_created_by, tclass.d_created_date as d_created_date, tclass.e_deleted as e_deleted, tclass.e_enabled as e_enabled, tclass.c_last_modified_by as c_last_modified_by, tclass.d_last_modified_date as d_last_modified_date, tclass.n_version as n_version, tclass.ID as tclass_id, evaluationanalysis.ID as evaluationanalysis_id, unit.ID as unit_id, tclass.C_CODE as tclass_c_code, tclass.F_INSTITUTE as institute_id, tclass.F_TEACHER as teacher_id, tclass.F_TERM as term_id, tclass.C_START_DATE as tclass_c_start_date, tclass.C_END_DATE as tclass_c_end_date, tclass.C_REASON as tclass_reason, tclass.C_EVALUATION as tclass_evaluation, tclass.c_teaching_type as tclass_teaching_type, tclass.f_holding_class_type_id as holding_class_type_id, holdingClassTypParameterValue.C_TITLE as holding_class_type_title, tclass.f_teaching_method_id as teaching_method_id, teachingMethodParameterValue.C_TITLE as teaching_method_title, SUBSTR(tclass.C_START_DATE, 1, 4) as tclass_year, course.C_CODE as course_c_code, course.E_TECHNICAL_TYPE as course_technical_type, course.E_RUN_TYPE as course_run_type, course.E_THEO_TYPE as course_theo_type, course.E_LEVEL_TYPE as course_level_type, course.CATEGORY_ID as course_category_id, course.SUBCATEGORY_ID as course_subcategory_id, course.C_TITLE_FA as course_c_title_fa, course.C_EVALUATION as course_c_evaluation, plnner_dep.C_MOJTAME_TITLE as planner_complex, plnner_dep.C_MOAVENAT_TITLE as planner_assistant, plnner_dep.C_OMOR_TITLE as planner_affairs, plnner_dep.C_GHESMAT_TITLE as planner_section, plnner_dep.C_VAHED_TITLE as planner_unit, CONCAT(CONCAT(personelPlanner.FIRST_NAME, ' '), personelPlanner.LAST_NAME) as planner_name, organizer_ins.C_TITLE_FA as institute_name, CASE WHEN students.counts IS NULL THEN 0 ELSE students.counts END AS tclass_students_count, evaluationanalysis.C_BEHAVIORAL_GRADE as evaluationanalysis_c_behavioral_grade, evaluationanalysis.B_BEHAVIORAL_PASS as evaluationanalysis_c_behavioral_pass, evaluationanalysis.C_EFFECTIVENESS_GRADE as evaluationanalysis_c_effectiveness_grade, evaluationanalysis.B_EFFECTIVENESS_PASS as evaluationanalysis_c_effectiveness_pass, evaluationanalysis.C_LEARNING_GRADE as evaluationanalysis_c_learning_grade, evaluationanalysis.B_LEARNING_PASS as evaluationanalysis_c_learning_pass, evaluationanalysis.C_REACTION_GRADE as evaluationanalysis_c_reaction_grade, evaluationanalysis.B_REACTION_PASS as evaluationanalysis_c_reaction_pass, evaluationanalysis.C_RESULTS_GRADE as evaluationanalysis_c_results_grade, evaluationanalysis.B_RESULTS_PASS as evaluationanalysis_c_results_pass, evaluationanalysis.C_TEACHER_GRADE as evaluationanalysis_c_teacher_grade, evaluationanalysis.B_TEACHER_PASS as evaluationanalysis_c_teacher_pass, CASE WHEN evaluationanalysis.C_REACTION_GRADE IS NULL THEN 0 ELSE 1 END as evaluationanalysis_b_reaction_status, CASE WHEN evaluationanalysis.C_BEHAVIORAL_GRADE IS NULL THEN 0 ELSE 1 END as evaluationanalysis_b_behavioral_status, CASE WHEN evaluationanalysis.C_LEARNING_GRADE IS NULL THEN 0 ELSE 1 END as evaluationanalysis_b_learning_status, CASE WHEN evaluationanalysis.C_RESULTS_GRADE IS NULL THEN 0 ELSE 1 END as evaluationanalysis_b_results_status, CASE WHEN evaluationanalysis.C_TEACHER_GRADE IS NULL THEN 0 ELSE 1 END as evaluationanalysis_b_teacher_status, CASE WHEN evaluationanalysis.C_EFFECTIVENESS_GRADE IS NULL THEN 0 ELSE 1 END as evaluationanalysis_b_effectiveness_status, STATISTICS_.PRESENCE, STATISTICS_.ABSENCE, STATISTICS_.UNKNOWN, supervisor.FIRST_NAME || supervisor.LAST_NAME as supervisor , percent.percent_reaction
FROM TBL_CLASS tclass INNER JOIN TBL_COURSE course ON tclass.F_COURSE = course.ID LEFT JOIN TBL_TARGET_SOCIETY unit ON unit.F_CLASS_ID = tclass.ID LEFT JOIN (SELECT TBL_CLASS_STUDENT.CLASS_ID, count(TBL_CLASS_STUDENT.ID) as counts FROM TBL_CLASS_STUDENT GROUP BY TBL_CLASS_STUDENT.CLASS_ID) students ON students.CLASS_ID = tclass.ID LEFT JOIN TBL_EVALUATION_ANALYSIS evaluationanalysis ON evaluationanalysis.F_TCLASS = tclass.ID INNER JOIN TBL_TERM term ON tclass.F_TERM = term.ID INNER JOIN TBL_TEACHER teacher ON tclass.F_TEACHER = teacher.ID LEFT JOIN TBL_PERSONAL_INFO personal ON teacher.F_PERSONALITY = personal.ID LEFT JOIN tbl_personnel personelPlanner on personelPlanner.id = tclass.f_planner LEFT JOIN tbl_department plnner_dep on plnner_dep.id = personelPlanner.f_department_id LEFT JOIN TBL_INSTITUTE organizer_ins on tclass.F_INSTITUTE_ORGANIZER = organizer_ins.ID LEFT JOIN TBL_PARAMETER_VALUE parameterValue on tclass.f_cancel_class_reason = parameterValue.ID LEFT JOIN TBL_PARAMETER_VALUE holdingClassTypParameterValue on tclass.f_holding_class_type_id = holdingClassTypParameterValue.ID LEFT JOIN TBL_PARAMETER_VALUE teachingMethodParameterValue on tclass.f_teaching_method_id = teachingMethodParameterValue.ID LEFT JOIN tbl_personnel supervisor on supervisor.id = tclass.F_SUPERVISOR LEFT JOIN( SELECT * FROM ( SELECT att_state, f_class_id, s_hour FROM ( SELECT CASE WHEN (att.c_state = 1 OR att.c_state = 2) THEN 'PRESENCE' WHEN (att.c_state = 3 OR att.c_state = 4) THEN 'ABSENCE' ELSE 'UNKNOWN' END AS att_state, att.f_session, att.f_student, sess.f_class_id, sess.s_hour FROM tbl_attendance att INNER JOIN ( SELECT (to_number(regexp_substr(c_session_end_hour, '[^:]+', 3, 1)) + 60 * to_number (regexp_substr(c_session_end_hour, '[^:]+', 1, 1)) - (to_number(regexp_substr (c_session_start_hour, '[^:]+', 3, 1)) + 60 * to_number(regexp_substr(c_session_start_hour , '[^:]+', 1, 1)))) / 60 AS s_hour, id, f_class_id FROM tbl_session ) sess ON (att.f_session = sess.id) ) ) PIVOT ( SUM(s_hour) FOR att_state IN ( 'PRESENCE' AS presence, 'ABSENCE' AS absence, 'UNKNOWN' AS unknown ) ) ) STATISTICS_ on (tclass.id = STATISTICS_.f_class_id) LEFT JOIN ( SELECT class_id AS class_id, CASE WHEN all_reaction_eval = 0 THEN 0 ELSE CAST((filled_reaction_eval / all_reaction_eval) * 100 AS DECIMAL(6, 2)) END AS percent_reaction FROM ( SELECT DISTINCT cs.class_id AS class_id, ( SELECT COUNT(DISTINCT cs.id) --AS filled_reaction_eval FROM tbl_class_student cs INNER JOIN tbl_class c ON cs.class_id = c.id WHERE c.id = classx.id AND cs.evaluation_status_reaction IN ( 2, 3 ) ) AS filled_reaction_eval, ( SELECT COUNT(DISTINCT cs.id) --AS all_reaction_eval FROM tbl_class_student cs INNER JOIN tbl_class c ON cs.class_id = c.id WHERE c.id = classx.id AND (cs.evaluation_status_reaction IN ( 0, 1, 2, 3 ) OR cs.evaluation_status_reaction IS NULL ) ) AS all_reaction_eval FROM tbl_class_student cs INNER JOIN tbl_class classx ON cs.class_id = classx.id )
) percent on tclass.id = percent.class_id

SELECT rowNum as id, term.C_TITLE_FA as term_TitleFa, personal.C_FIRST_NAME_FA as teacher_FirstName, personal.C_LAST_NAME_FA as teacher_LastName, CONCAT(CONCAT(personal.C_FIRST_NAME_FA, ' '), personal.C_LAST_NAME_FA) as teacher_FullName, tclass.N_H_DURATION as tclass_n_duration, tclass.F_INSTITUTE_ORGANIZER as tclass_organizer, tclass.C_STATUS as tclass_status, tclass.f_cancel_class_reason as class_cancel_reason_id, parameterValue.C_TITLE as class_cancel_reason_title, tclass.F_PLANNER as tclass_planner, tclass.F_SUPERVISOR as tclass_supervisor, tclass.c_created_by as c_created_by, tclass.d_created_date as d_created_date, tclass.e_deleted as e_deleted, tclass.e_enabled as e_enabled, tclass.c_last_modified_by as c_last_modified_by, tclass.d_last_modified_date as d_last_modified_date, tclass.n_version as n_version, tclass.ID as tclass_id, evaluationanalysis.ID as evaluationanalysis_id, unit.ID as unit_id, tclass.C_CODE as tclass_c_code, tclass.F_INSTITUTE as institute_id, tclass.F_TEACHER as teacher_id, tclass.F_TERM as term_id, tclass.C_START_DATE as tclass_c_start_date, tclass.C_END_DATE as tclass_c_end_date, tclass.C_REASON as tclass_reason, tclass.C_EVALUATION as tclass_evaluation, tclass.c_teaching_type as tclass_teaching_type, tclass.f_holding_class_type_id as holding_class_type_id, holdingClassTypParameterValue.C_TITLE as holding_class_type_title, tclass.f_teaching_method_id as teaching_method_id, teachingMethodParameterValue.C_TITLE as teaching_method_title, SUBSTR(tclass.C_START_DATE, 1, 4) as tclass_year, course.C_CODE as course_c_code, course.E_TECHNICAL_TYPE as course_technical_type, course.E_RUN_TYPE as course_run_type, course.E_THEO_TYPE as course_theo_type, course.E_LEVEL_TYPE as course_level_type, course.CATEGORY_ID as course_category_id, course.SUBCATEGORY_ID as course_subcategory_id, course.C_TITLE_FA as course_c_title_fa, course.C_EVALUATION as course_c_evaluation, plnner_dep.C_MOJTAME_TITLE as planner_complex, plnner_dep.C_MOAVENAT_TITLE as planner_assistant, plnner_dep.C_OMOR_TITLE as planner_affairs, plnner_dep.C_GHESMAT_TITLE as planner_section, plnner_dep.C_VAHED_TITLE as planner_unit, CONCAT(CONCAT(personelPlanner.FIRST_NAME, ' '), personelPlanner.LAST_NAME) as planner_name, organizer_ins.C_TITLE_FA as institute_name, CASE WHEN students.counts IS NULL THEN 0 ELSE students.counts END AS tclass_students_count, evaluationanalysis.C_BEHAVIORAL_GRADE as evaluationanalysis_c_behavioral_grade, evaluationanalysis.B_BEHAVIORAL_PASS as evaluationanalysis_c_behavioral_pass, CAST(evaluationanalysis.C_EFFECTIVENESS_GRADE AS DECIMAL(6,2)) as evaluationanalysis_c_effectiveness_grade, evaluationanalysis.B_EFFECTIVENESS_PASS as evaluationanalysis_c_effectiveness_pass, evaluationanalysis.C_LEARNING_GRADE as evaluationanalysis_c_learning_grade, evaluationanalysis.B_LEARNING_PASS as evaluationanalysis_c_learning_pass, CAST(evaluationanalysis.C_REACTION_GRADE AS DECIMAL(6,2)) as evaluationanalysis_c_reaction_grade, evaluationanalysis.B_REACTION_PASS as evaluationanalysis_c_reaction_pass, evaluationanalysis.C_RESULTS_GRADE as evaluationanalysis_c_results_grade, evaluationanalysis.B_RESULTS_PASS as evaluationanalysis_c_results_pass, CAST(evaluationanalysis.C_TEACHER_GRADE AS DECIMAL(6,2)) as evaluationanalysis_c_teacher_grade, evaluationanalysis.B_TEACHER_PASS as evaluationanalysis_c_teacher_pass, CASE WHEN evaluationanalysis.C_REACTION_GRADE IS NULL THEN 0 ELSE 1 END as evaluationanalysis_b_reaction_status, CASE WHEN evaluationanalysis.C_BEHAVIORAL_GRADE IS NULL THEN 0 ELSE 1 END as evaluationanalysis_b_behavioral_status, CASE WHEN evaluationanalysis.C_LEARNING_GRADE IS NULL THEN 0 ELSE 1 END as evaluationanalysis_b_learning_status, CASE WHEN evaluationanalysis.C_RESULTS_GRADE IS NULL THEN 0 ELSE 1 END as evaluationanalysis_b_results_status, CASE WHEN evaluationanalysis.C_TEACHER_GRADE IS NULL THEN 0 ELSE 1 END as evaluationanalysis_b_teacher_status, CASE WHEN evaluationanalysis.C_EFFECTIVENESS_GRADE IS NULL THEN 0 ELSE 1 END as evaluationanalysis_b_effectiveness_status, STATISTICS_.PRESENCE, STATISTICS_.ABSENCE, STATISTICS_.UNKNOWN, supervisor.FIRST_NAME || supervisor.LAST_NAME as supervisor , Reactionpercent.percent_reaction , behevioralpercent.percent_behavior
FROM TBL_CLASS tclass INNER JOIN TBL_COURSE course ON tclass.F_COURSE = course.ID LEFT JOIN TBL_TARGET_SOCIETY unit ON unit.F_CLASS_ID = tclass.ID LEFT JOIN (SELECT TBL_CLASS_STUDENT.CLASS_ID, count(TBL_CLASS_STUDENT.ID) as counts FROM TBL_CLASS_STUDENT GROUP BY TBL_CLASS_STUDENT.CLASS_ID) students ON students.CLASS_ID = tclass.ID LEFT JOIN TBL_EVALUATION_ANALYSIS evaluationanalysis ON evaluationanalysis.F_TCLASS = tclass.ID INNER JOIN TBL_TERM term ON tclass.F_TERM = term.ID INNER JOIN TBL_TEACHER teacher ON tclass.F_TEACHER = teacher.ID LEFT JOIN TBL_PERSONAL_INFO personal ON teacher.F_PERSONALITY = personal.ID LEFT JOIN tbl_personnel personelPlanner on personelPlanner.id = tclass.f_planner LEFT JOIN tbl_department plnner_dep on plnner_dep.id = personelPlanner.f_department_id LEFT JOIN TBL_INSTITUTE organizer_ins on tclass.F_INSTITUTE_ORGANIZER = organizer_ins.ID LEFT JOIN TBL_PARAMETER_VALUE parameterValue on tclass.f_cancel_class_reason = parameterValue.ID LEFT JOIN TBL_PARAMETER_VALUE holdingClassTypParameterValue on tclass.f_holding_class_type_id = holdingClassTypParameterValue.ID LEFT JOIN TBL_PARAMETER_VALUE teachingMethodParameterValue on tclass.f_teaching_method_id = teachingMethodParameterValue.ID LEFT JOIN tbl_personnel supervisor on supervisor.id = tclass.F_SUPERVISOR LEFT JOIN( SELECT * FROM ( SELECT att_state, f_class_id, s_hour FROM ( SELECT CASE WHEN (att.c_state = 1 OR att.c_state = 2) THEN 'PRESENCE' WHEN (att.c_state = 3 OR att.c_state = 4) THEN 'ABSENCE' ELSE 'UNKNOWN' END AS att_state, att.f_session, att.f_student, sess.f_class_id, sess.s_hour FROM tbl_attendance att INNER JOIN ( SELECT (to_number(regexp_substr(c_session_end_hour, '[^:]+', 3, 1)) + 60 * to_number (regexp_substr(c_session_end_hour, '[^:]+', 1, 1)) - (to_number(regexp_substr (c_session_start_hour, '[^:]+', 3, 1)) + 60 * to_number(regexp_substr(c_session_start_hour , '[^:]+', 1, 1)))) / 60 AS s_hour, id, f_class_id FROM tbl_session ) sess ON (att.f_session = sess.id) ) ) PIVOT ( SUM(s_hour) FOR att_state IN ( 'PRESENCE' AS presence, 'ABSENCE' AS absence, 'UNKNOWN' AS unknown ) ) ) STATISTICS_ on (tclass.id = STATISTICS_.f_class_id) LEFT JOIN ( SELECT class_id AS class_id, CASE WHEN all_reaction_eval = 0 THEN 0 ELSE CAST((filled_reaction_eval / all_reaction_eval) * 100 AS DECIMAL(6, 2)) END AS percent_reaction FROM ( SELECT DISTINCT cs.class_id AS class_id, ( SELECT COUNT(DISTINCT cs.id) --AS filled_reaction_eval FROM tbl_class_student cs INNER JOIN tbl_class c ON cs.class_id = c.id WHERE c.id = classx.id AND cs.evaluation_status_reaction IN ( 2, 3 ) ) AS filled_reaction_eval, ( SELECT COUNT(DISTINCT cs.id) --AS all_reaction_eval FROM tbl_class_student cs INNER JOIN tbl_class c ON cs.class_id = c.id WHERE c.id = classx.id AND (cs.evaluation_status_reaction IN ( 0, 1, 2, 3 ) OR cs.evaluation_status_reaction IS NULL ) ) AS all_reaction_eval FROM tbl_class_student cs INNER JOIN tbl_class classx ON cs.class_id = classx.id )
) Reactionpercent on tclass.id = Reactionpercent.class_id LEFT JOIN ( SELECT class_id AS class_id, CASE WHEN all_behavioral_eval = 0 THEN 0 ELSE CAST((filled_behavior_eval / all_behavioral_eval) * 100 AS DECIMAL(6, 2)) END AS percent_behavior FROM ( SELECT DISTINCT class.id AS class_id, ( SELECT COUNT(DISTINCT ex.id) FROM tbl_class cx INNER JOIN tbl_evaluation ex ON ex.f_class_id = cx.id OUTER APPLY ( SELECT id AS id FROM tbl_parameter_value v WHERE v.c_code = 'Behavioral' ) raftari WHERE 1 = 1 AND class.id = cx.id AND ex.f_evaluation_level_id = raftari.id -- 156 AND ex.b_status = 1 ) AS filled_behavior_eval, ( SELECT COUNT(DISTINCT ex.id) FROM tbl_class cx INNER JOIN tbl_evaluation ex ON ex.f_class_id = cx.id OUTER APPLY ( SELECT id AS id FROM tbl_parameter_value v WHERE v.c_code = 'Behavioral' ) raftari WHERE 1 = 1 AND class.id = cx.id AND ex.f_evaluation_level_id = raftari.id -- 156 ) AS all_behavioral_eval FROM tbl_class class INNER JOIN tbl_evaluation e ON e.f_class_id = class.id OUTER APPLY ( SELECT id AS id FROM tbl_parameter_value v WHERE v.c_code = 'Behavioral' ) raftari_id WHERE 1 = 1 AND e.f_evaluation_level_id = raftari_id.id -- 156 ) ) behevioralpercent on tclass.id = behevioralpercent.class_id
